mapper.select:
SELECT BOOK_STRT_DT , BOOK_END_DT , BOOK_CD , B.NAME AS locNm , CMN_CD AS bookSttsCd , CMN_NM AS bookSttsNm , A.NAME , AES_DECRYPT(UNHEX(A.TEL), 'grape') TEL , AES_DECRYPT(UNHEX(EMAIL), 'grape') EMAIL , BOOK_AMT , DC_AMT , PAY_AMT , BOOK_REQ_DT , B.LOC_SEQ , B.BANK_NM , B.ACCNT_NO , B.CMPY_TP FROM BOOK A INNER JOIN LOCATION B ON A.LOC_SEQ = B.LOC_SEQ INNER JOIN CMN_CODE C ON A.BOOK_STTS_CD = C.CMN_CD WHERE 1=1
AND (BOOK_STRT_DT BETWEEN #{strtDt} AND #{endDt} OR BOOK_END_DT BETWEEN #{strtDt} AND #{endDt})
AND B.LOC_SEQ = #{locSeq}
AND BOOK_CD = #{bookCd}
AND A.NAME = #{name}
AND AES_DECRYPT(UNHEX(EMAIL), 'grape') = #{email}
ORDER BY BOOK_CD DESC

mapper.insert:
INSERT INTO BOOK (
      BOOK_CD
    , LOC_SEQ
    , NAME
    , EMAIL
    , TEL
    , BOOK_STRT_DT
    , BOOK_END_DT
    , BOOK_REQ_DT
    , BOOK_STTS_CD
    , BOOK_AMT
    , DC_AMT
    , PAY_AMT
    , REQ_CNTS
    , REG_DT
) VALUES (
      #{bookCd}
    , #{locSeq}
    , #{name}
    , HEX(AES_ENCRYPT(#{email}, 'grape'))
    , HEX(AES_ENCRYPT(#{tel}, 'grape'))
    , #{bookStrtDt}
    , #{bookEndDt}
    , CURRENT_TIMESTAMP
    , #{bookSttsCd}
    , #{bookAmt}
    , #{dcAmt}
    , #{payAmt}
    , #{reqCnts}
    , CURRENT_TIMESTAMP
)

mapper.update:
        UPDATE BOOK
           SET UPD_DT    = CURRENT_TIMESTAMP

			 , UPD_ID    = #{updId}


			 , BOOK_STTS_CD = #{bookSttsCd}


			 , NAME = #{name}


			 , TEL = HEX(AES_ENCRYPT(#{tel}, 'grape'))


			 , EMAIL = HEX(AES_ENCRYPT(#{email}, 'grape'))


			 , BOOK_AMT = #{bookAmt}


			 , PAY_AMT = #{payAmt}


			 , DC_AMT = #{dcAmt}


			 , REQ_CNTS = #{reqCnts}

      	 WHERE BOOK_CD = #{bookCd}
